import React from 'react';
import { marked } from 'marked';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

interface AppState {
	text: string;
}

class App extends React.Component<{}, AppState> {
	constructor(props: {}) {
		super(props);
		this.state = {
			text: "# Welcome to my React Markdown Previewer! This is a sub-heading... And here's some other cool stuff:"
		}
		this.handleChange = this.handleChange.bind(this);
	}
	handleChange(event: React.ChangeEvent<HTMLTextAreaElement>) {
		this.setState({text: event.target.value });
	}
	createMarkup() {
		return { __html: marked.parse(this.state.text) as string };
	}
	render() {
		return (
			<>
				<div id="editorWrapper">
    				<textarea className="form-control" id="editor" rows={10} onChange={this.handleChange} value={this.state.text}></textarea>
				</div>
    			<div id="preview" dangerouslySetInnerHTML={this.createMarkup()}></div>
			</>
		);
	}
}

export default App;
